    public StorageResource getAllGenes(String dna) {
        StorageResource geneList = new StorageResource();
        int startIndex = 0;
        while (true) {
            String currentGene = findGene(dna, startIndex);
            if (currentGene.isEmpty()) break;
            System.out.println(currentGene);
            geneList.add(currentGene);
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
            }
        return geneList;
    }
    
    public float cgRatio(String dna) {
        dna = dna.toLowerCase();
        int cg = 0;
        int lenDna = dna.length();
        if (lenDna < 1) return (float) -0.1;
        for (int i = 0; i < dna.length(); i++) {
            if (dna.charAt(i) == 'c' || dna.charAt(i) == 'g') cg++;
        }
        float ratio = (float) cg / lenDna;
        return ratio;
    }
    
    public int countCTG(String dna) {
        dna = dna.toLowerCase();
        int ctg = 0;
        int index = dna.indexOf("ctg");
        while (true) {
            if (index == -1 || index > dna.length() - 3) break;
            ctg++;
            //String found = dna.substring(index, index + 3);
            index = dna.indexOf("ctg", index + 3);
        }
        return ctg;
    }
    
    public void processGenes(StorageResource sr) {
        int lengthLongestGene = -1;
        StorageResource longerThan9 = new StorageResource();
        StorageResource cgRatiogt35 = new StorageResource();
        for (String gene : sr.data()) {
            if (gene.length() > 9) longerThan9.add(gene);
            if (cgRatio(gene) > 0.35) cgRatiogt35.add(gene);
            if (gene.length() > lengthLongestGene) lengthLongestGene = gene.length();
        }
        
        for (String gene : longerThan9.data()) {
            System.out.println(gene);
        }
        System.out.println(longerThan9.size());
        
        for (String gene : cgRatiogt35.data()) {
            System.out.println(gene);
        }
        System.out.println(cgRatiogt35.size());    
        
        System.out.println("Length of longest gene " + lengthLongestGene);
    }
    
